css:
@media all {
   div .printedby {
       position: fixed;
       bottom: 0;
       text-align: right;
       font-size: xx-small;
       width: 100% !important;
   }

   div #main {
	margin-left: 2cm;
	margin-right: 2cm;
   }
}

html:
{%- if letter_head and not no_letterhead -%}
	<div class="letter-head">{{ letter_head }}</div>
{%- endif -%}
<div id="main">
<div  style="text-align: center;">
<h3>PURCHASE ORDER</h3>
<h5><u>
{%- if doc.items[0].cost_center -%}
	{{ doc.items[0].cost_center }}
{%- endif -%}
</u></h5>
<span style="font-size: small;">The following purchase order number should appear in all related correspondences:</span> <br /><strong>{{ doc.name }} dated {{ frappe.utils.formatdate(doc.transaction_date) }}</strong>
</div>
<div style="margin-top: 40px;"></div>
<div style="float: left; width: 50%;">To:<br />
<p>Ms. {{ doc.supplier_name }}</p>
<p>{{ frappe.db.get_value("Supplier", doc.supplier_name, "location") }}</p>
{%- if frappe.db.get_value("Supplier", doc.supplier_name, "telephone_and_fax")  -%}
<p>Tel/Fax: {{ frappe.db.get_value("Supplier", doc.supplier_name, "telephone_and_fax") }}</p>
{%- endif -%}
</div>

{%- if doc.deliver_to  -%}
<div style="float: right; text-align: left;  width: 50%;">Ship To:<br />
{{ frappe.db.get_value("Branch", doc.deliver_to, "address") }}
</div>
{%- endif -%}

<div style="clear: both;"><br /><p>Dear Sir/Madam,</p><br />
{%- if doc.po_header -%}
{{ doc.po_header }}
{%- endif -%}
</div>
<div style="padding-top: 20px;">
<table class="table table-bordered" style="margin-top: 15px; font-size: small">
<th style="width:38.5pt;">Sl. No</th>
<th style="width:56.75pt;">Material Code</th>
<th style="width:130.5pt;">Material Name</th>
<th style="width:45.0pt;">Unit</th>
<th style="width:49.5pt;">Qty</th>
<th style="width:63pt;">Rate </th>
<th style="width:81pt;">Amount </th>

{%- for row in doc.items -%}
<tr>
<td>{{ row.idx }}</td>
<td>{{ row.item_code }}</td>
<td>{{ row.item_name }}</td>
<td>{{ row.uom }}</td>
<td>{{ row.qty }}</td>
<td style="text-align: right">{{ frappe.format_value(row.rate, {"fieldtype":"Currency"})}}</td>
<td style="text-align: right">{{ frappe.format_value(row.amount, {"fieldtype":"Currency"}) }}</td>
</tr>
{%- endfor -%} 

{%- if doc.discount_amount -%}
<tr>
<td colspan="6" style="text-align:right;">{{ doc.additional_cost_or_discount_description }}
</td>
<td style="text-align:right;">
{{ frappe.format_value(doc.discount_amount * -1, {"fieldtype":"Currency"}) }}
</td>
</tr>
{%- endif -%}

<tr>
<td colspan="6" style="text-align:right;"><b>Total</b>
</td>
<td style="text-align:right;">
{{ frappe.format_value(doc.grand_total, {"fieldtype":"Currency"}) }}
</td>
</tr>
</table>
</div>
{%- if doc.terms -%}
<br /><p>Purchase Terms &amp; Conditions:</p>
{{ doc.terms }}
{%- endif -%}
<br />
{%- if doc.po_footer_text -%}
{{ doc.po_footer_text }}
{%- endif -%}
</div>

<div class="printedby">This print is system generated and should be considered VALID only with appropriate seal and/or signature. <br>  Printed by {{ frappe.user }} on {{ frappe.utils.nowdate()  }} at {{ frappe.utils.nowtime() }}</div>
